                    console.log(this.props.comments)

                }
                <form className="comment-form" onSubmit={this.handleSubmit}>
                    <input type="text" placeholder="comment" name="comment" />
                    <input type="submit" hidden />
                </form>
            </div>
        )
    }
}

export default Comments;
